// File: DLLa.cpp\n\n#include <windows.h>\n#include <iostream>\n\n// Forward declaration of functions in DLL B\nextern "C" void fooB();\nextern "C" void fooBB();\n\nextern "C" __declspec(dllexport) void fooA() {\n    fooBB();\n}\n\nextern "C" __declspec(dllexport) void fooAA() {\n    fooB();\n}\n\n// Entry point for DLL\nBOOL APIENTRY DllMain(HMODULE hModule, DWORD  ul_reason_for_call, LPVOID lpReserved) {\n    switch (ul_reason_for_call) {\n    case DLL_PROCESS_ATTACH:\n    case DLL_THREAD_ATTACH:\n    case DLL_THREAD_DETACH:\n    case DLL_PROCESS_DETACH:\n        break;\n    }\n    return TRUE;\n}