#include <iostream>\n#include <random>\n\nuint32_t generateRandomID() {\n    // Create a random device and random number generator\n    std::random_device rd;\n    std::mt19937 gen(rd());\n\n    // Define the range for the 32-bit number\n    std::uniform_int_distribution<uint32_t> dis(0, UINT32_MAX);\n\n    // Generate a random 32-bit number\n    return dis(gen);\n}\n\nint main() {\n    // Generate a 32-bit random ID\n    uint32_t id = generateRandomID();\n\n    // Print the 32-bit random ID\n    std::cout << std::hex << id << std::endl;\n\n    return 0;\n}