#include <iostream>\n#include <string>\n\nstd::string wordWrap(std::string sentence, int width)\n{\n    std::string::iterator it = sentence.begin();\n    std::string::iterator lastSpace = sentence.begin();\n\n    int distanceToWidth = 0;\n\n    while (it != sentence.end())\n    {\n        while (it != sentence.end() && distanceToWidth < width)\n        {\n            if (*it == ' ')\n            {\n                lastSpace = it;\n            }\n\n            distanceToWidth++;\n            it++;\n        }\n\n        if (distanceToWidth >= width)\n        {\n            if (lastSpace == sentence.begin() || lastSpace == sentence.end())\n            {\n                // If no space was found, we have to break the word at width\n                it = sentence.begin() + distanceToWidth;\n                lastSpace = it;\n            }\n\n            *lastSpace = '\n';\n            distanceToWidth = 0;\n        }\n    }\n\n    return sentence;\n}\n\nint main()\n{\n    std::cout << wordWrap("a b c abcde abcdef longword shtwd", 5) << std::endl << std::endl;\n    std::cout << wordWrap("this is a sentence of massive proportions", 4) << std::endl;\n    return 0;\n}