// Callback function pointer\ntypedef void (*LabVIEWCallback)(int newData);\n\n// Global variable to store the callback function pointer\nLabVIEWCallback g_callback = nullptr;\n\n// Function to register the callback from LabVIEW\nextern "C" __declspec(dllexport) void RegisterCallback(LabVIEWCallback callback) {\n    g_callback = callback;\n}\n\n// Function to notify LabVIEW when new data is available\nvoid NotifyLabVIEW(int newData) {\n    if (g_callback != nullptr) {\n        g_callback(newData);\n    }\n}