// Adder.h\n#pragma once\nclass Adder {\npublic:\n    int Add(int a, int b);\n};\n\n// Adder.cpp\n#include "Adder.h"\nint Adder::Add(int a, int b) {\n    return a + b;\n}\n\n// AdderTest.cpp\n#include "gtest/gtest.h"\n#include "Adder.h"\n\nTEST(AdderTest, AddFunction) {\n    Adder adder;\n    EXPECT_EQ(adder.Add(1, 1), 2);\n    EXPECT_EQ(adder.Add(-1, 1), 0);\n    EXPECT_EQ(adder.Add(-1, -1), -2);\n    EXPECT_EQ(adder.Add(0, 0), 0);\n}\n\nint main(int argc, char **argv) {\n    ::testing::InitGoogleTest(&argc, argv);\n    return RUN_ALL_TESTS();\n}