#include <cstdlib>\n\nint main() {\n    // Command to convert HTML file to PDF using wkhtmltopdf\n    const char* cmd = "wkhtmltopdf input.html output.pdf";\n    \n    // Execute the command\n    int result = std::system(cmd);\n    \n    if (result == 0) {\n        // Conversion was successful\n        std::cout << "PDF created successfully." << std::endl;\n    } else {\n        // Conversion failed\n        std::cerr << "Failed to create PDF." << std::endl;\n    }\n\n    return result;\n}