// Example of creating named shared memory\n   HANDLE hMapFile = CreateFileMapping(\n       INVALID_HANDLE_VALUE,    // use paging file\n       NULL,                    // default security\n       PAGE_READWRITE,          // read/write access\n       0,                       // maximum object size (high-order DWORD)\n       sizeof(MyClass),         // maximum object size (low-order DWORD)\n       TEXT("Local\\MySharedMemory")); // name of mapping object\n\n   if (hMapFile == NULL) {\n       // Handle error\n   }\n\n   // Map the named shared memory to the process's address space\n   MyClass* pSharedData = (MyClass*) MapViewOfFile(hMapFile, FILE_MAP_ALL_ACCESS, 0, 0, sizeof(MyClass));\n   if (pSharedData == NULL) {\n       // Handle error\n   }\n\n   // Use pSharedData to read/write shared data