#include <cstdio>\n#include <cstdlib>\n\nint main() {\n    FILE *file = fopen("yourfile.txt", "r");\n    if (file == nullptr) {\n        perror("Failed to open file");\n        return EXIT_FAILURE;\n    }\n\n    char str[1024];\n    while (fgets(str, sizeof(str), file) != nullptr) {\n        // Process the line stored in str\n        printf("%s", str); // or any other processing\n    }\n\n    if (ferror(file)) {\n        perror("Error reading from file");\n    }\n\n    fclose(file);\n    return EXIT_SUCCESS;\n}