#include <cstdio>\n#include <cstdlib>\n\nint main() {\n    FILE *file = fopen("yourfile.txt", "r");\n    if (file == nullptr) {\n        perror("Failed to open file");\n        return EXIT_FAILURE;\n    }\n\n    char *line = nullptr;\n    size_t len = 0;\n    ssize_t read;\n\n    while ((read = getline(&line, &len, file)) != -1) {\n        // Process the line\n        printf("%s", line); // or any other processing\n    }\n\n    if (ferror(file)) {\n        perror("Error reading from file");\n    }\n\n    free(line);\n    fclose(file);\n    return EXIT_SUCCESS;\n}