#include <iostream>\n#include <fstream>\n\nusing namespace std;\n\nbool checkName( const char *cFileName );\nifstream* openInputFile( const char *cFileName );\n\nint main( const int argc, const char **argv ) {\n    if (argc < 2) {\n        cout << "Usage: " << argv[0] << " <filename>" << endl;\n        return 1;\n    }\n\n    const char *cFileName = argv[1];\n    if (!checkName(cFileName)) {\n        return 1;\n    }\n\n    // ... rest of your code ...\n\n    return 0;\n}\n\nifstream* openInputFile( const char *cFileName ) {\n    ifstream *inFile = new ifstream(cFileName, ios::in);\n    if (!inFile->is_open()) {\n        delete inFile;\n        return nullptr;\n    }\n    return inFile;\n}\n\nbool checkName( const char *cFileName ) {\n    ifstream *inFile = openInputFile(cFileName);\n    if (inFile == nullptr) {\n        cout << '"' << cFileName << '"' << ": File does not exist!" << endl;\n        return false;\n    }\n\n    bool exists = inFile->good();\n    delete inFile;  // Clean up the allocated memory\n    return exists;\n}