#include <comutil.h>\n#include <iostream>\n\n// Include the library for _variant_t\n#pragma comment(lib, "comsuppw.lib")\n\nint main() {\n    // Sample code to illustrate conversion\n    _variant_t resultsDataString;\n\n    // Assuming resultsDataString is already populated with a VARIANT of type VT_BSTR\n    resultsDataString = L"12345";\n\n    // Check the type of the variant\n    if (resultsDataString.vt == VT_BSTR) {\n        // Convert BSTR to std::wstring\n        std::wstring ws(resultsDataString.bstrVal, SysStringLen(resultsDataString.bstrVal));\n        std::wcout << L"Extracted string: " << ws << std::endl;\n\n        // Optionally, convert std::wstring to std::string if needed\n        std::string str(ws.begin(), ws.end());\n        std::cout << "Converted to std::string: " << str << std::endl;\n    } else {\n        std::cerr << "Unexpected variant type." << std::endl;\n    }\n\n    return 0;\n}