#include <windows.h>\n#include <iostream>\n\ntypedef void (__stdcall *PFN_SAYHELLO)();\n\nint main()\n{\n    HINSTANCE lib = LoadLibrary("dllcode.dll");\n    if (!lib) {\n        DWORD error = GetLastError();\n        std::cout << "LoadLibrary failed with error code: " << error << std::endl;\n        return 1;\n    }\n\n    PFN_SAYHELLO sayHello = (PFN_SAYHELLO)GetProcAddress(lib, "sayHello");\n    if (!sayHello) {\n        DWORD error = GetLastError();\n        std::cout << "GetProcAddress failed with error code: " << error << std::endl;\n        return 1;\n    }\n\n    sayHello();\n    return 0;\n}