#include <iostream>\n#include <uuid/uuid.h>\n\nint main() {\n    uuid_t uuid;\n    char uuid_str[37]; // UUID strings are 36 characters plus the null terminator\n\n    // Generate a new UUID\n    uuid_generate(uuid);\n\n    // Convert the UUID to a string\n    uuid_unparse(uuid, uuid_str);\n\n    // Output the UUID string\n    std::cout << "Generated UUID: " << uuid_str << std::endl;\n\n    return 0;\n}