#include <windows.h>\n#include <winnetwk.h>\n#include <iostream>\n\nvoid DisconnectNetworkDrive(const std::string& driveLetter, bool force)\n{\n    DWORD result = WNetCancelConnection2(driveLetter.c_str(), CONNECT_UPDATE_PROFILE, force);\n    if (result == NO_ERROR) {\n        std::cout << "Disconnected successfully." << std::endl;\n    } else {\n        std::cerr << "Failed to disconnect. Error code: " << result << std::endl;\n    }\n}\n\nint main()\n{\n    std::string driveLetter = "Z:"; // Change this to your specific drive letter\n    bool force = true; // Set to true if you want to force the disconnection\n\n    DisconnectNetworkDrive(driveLetter, force);\n\n    return 0;\n}