#include <windows.h>\n#include <shlobj.h>\n#include <iostream>\n\nint main() {\n    char path[MAX_PATH];\n    HRESULT result = SHGetFolderPath(NULL, CSIDL_COMMON_APPDATA, NULL, 0, path);\n\n    if (result == S_OK) {\n        std::cout << "Common AppData Path: " << path << std::endl;\n        // Now you can use this path to read/write your application data.\n    } else {\n        std::cerr << "Failed to get Common AppData Path" << std::endl;\n    }\n\n    return 0;\n}