#include <windows.h>\n#include <iostream>\n#include <vector>\n\nvoid GetFileVersion(const std::wstring& filePath) {\n    DWORD handle = 0;\n    DWORD size = GetFileVersionInfoSizeW(filePath.c_str(), &handle);\n    if (size == 0) {\n        std::wcerr << L"Error getting version info size: " << GetLastError() << std::endl;\n        return;\n    }\n\n    std::vector<BYTE> versionInfo(size);\n    if (!GetFileVersionInfoW(filePath.c_str(), handle, size, versionInfo.data())) {\n        std::wcerr << L"Error getting version info: " << GetLastError() << std::endl;\n        return;\n    }\n\n    VS_FIXEDFILEINFO* fileInfo = nullptr;\n    UINT fileInfoSize = 0;\n    if (!VerQueryValueW(versionInfo.data(), L"\\", reinterpret_cast<LPVOID*>(&fileInfo), &fileInfoSize)) {\n        std::wcerr << L"Error querying version value: " << GetLastError() << std::endl;\n        return;\n    }\n\n    if (fileInfoSize > 0 && fileInfo != nullptr) {\n        std::wcout << L"File Version: "\n                   << HIWORD(fileInfo->dwFileVersionMS) << L"."\n                   << LOWORD(fileInfo->dwFileVersionMS) << L"."\n                   << HIWORD(fileInfo->dwFileVersionLS) << L"."\n                   << LOWORD(fileInfo->dwFileVersionLS) << std::endl;\n    } else {\n        std::wcerr << L"File version info not found." << std::endl;\n    }\n}\n\nint main() {\n    std::wstring filePath = L"C:\\Path\\To\\Your\\DLLOrEXE.dll";  // Replace with your file path\n    GetFileVersion(filePath);\n    return 0;\n}