#include <iostream>\n   #include <SQLiteCpp/SQLiteCpp.h>\n\n   int main()\n   {\n       try\n       {\n           // Open a database file\n           SQLite::Database db("example.db3", SQLite::OPEN_READWRITE | SQLite::OPEN_CREATE);\n\n           // Execute a query\n           db.exec("CREATE TABLE IF NOT EXISTS config (key TEXT PRIMARY KEY, value TEXT)");\n\n           // Insert a value\n           SQLite::Statement query(db, "INSERT OR REPLACE INTO config (key, value) VALUES (?, ?)");\n           query.bind(1, "example_key");\n           query.bind(2, "example_value");\n           query.exec();\n\n           // Select and display values\n           SQLite::Statement select(db, "SELECT key, value FROM config");\n           while (select.executeStep())\n           {\n               std::cout << "Key: " << select.getColumn(0) << ", Value: " << select.getColumn(1) << std::endl;\n           }\n       }\n       catch (const std::exception& e)\n       {\n           std::cerr << "SQLite error: " << e.what() << std::endl;\n           return 1;\n       }\n\n       return 0;\n   }