#include <iostream>\n#include "OpenHardwareMonitorLib.h" // Make sure this path is correct for your setup\n\nint main() {\n    // Initialize the library\n    OHM::HardwareMonitor monitor;\n    \n    // Update the sensor values\n    monitor.Update();\n\n    // Iterate through available hardware\n    for (int i = 0; i < monitor.GetHardwareCount(); ++i) {\n        const OHM::Hardware* hardware = monitor.GetHardware(i);\n        \n        // Check if the hardware is a CPU\n        if (hardware->GetHardwareType() == OHM::HardwareType::CPU) {\n            std::cout << "Found CPU: " << hardware->GetName() << std::endl;\n\n            // Iterate through sensors to find the fan speed control\n            for (int j = 0; j < hardware->GetSensorCount(); ++j) {\n                const OHM::Sensor* sensor = hardware->GetSensor(j);\n                \n                if (sensor->GetSensorType() == OHM::SensorType::Fan) {\n                    std::cout << "Fan speed: " << sensor->GetValue() << " RPM" << std::endl;\n\n                    // Example: To set the fan speed (not all sensors support this)\n                    // sensor->SetValue(desired_speed);\n                }\n            }\n        }\n    }\n\n    return 0;\n}