#include <soci/soci.h>\n#include <soci/odbc/soci-odbc.h>\n#include <iostream>\n#include <string>\n\nint main() {\n    try {\n        // Create a session object for connecting to Microsoft SQL Server\n        soci::session sql(soci::odbc, "DSN=YourDSN;UID=YourUsername;PWD=YourPassword");\n\n        // Example query\n        std::string version;\n        sql << "SELECT @@VERSION", soci::into(version);\n\n        std::cout << "SQL Server Version: " << version << std::endl;\n    }\n    catch (const std::exception &e) {\n        std::cerr << "Error: " << e.what() << std::endl;\n    }\n\n    return 0;\n}