#include <prince.h>\n\nint main() {\n    // Create a Prince instance\n    Prince prince;\n\n    // Set input and output files\n    prince.setInput("input.html");\n    prince.setOutput("output.pdf");\n\n    // Convert HTML to PDF\n    if (prince.convert()) {\n        std::cout << "PDF created successfully." << std::endl;\n    } else {\n        std::cerr << "Failed to create PDF." << std::endl;\n    }\n\n    return 0;\n}