#include <stxxl/vector>\n#include <iostream>\n\nint main() {\n    // Create a disk-backed vector\n    stxxl::vector<int> vec;\n\n    // Insert elements\n    for (int i = 0; i < 1000000; ++i) {\n        vec.push_back(i);\n    }\n\n    // Access elements\n    for (stxxl::vector<int>::const_iterator it = vec.begin(); it != vec.end(); ++it) {\n        std::cout << *it << " ";\n    }\n\n    return 0;\n}