#include <iostream>\n#include <ctime>\n\nvoid yourFunction() {\n    // Your function code here\n}\n\nint main() {\n    clock_t start, end;\n    double cpu_time_used;\n\n    start = clock();\n    yourFunction();\n    end = clock();\n\n    cpu_time_used = ((double) (end - start)) / CLOCKS_PER_SEC;\n\n    std::cout << "Function execution time: " << cpu_time_used << " seconds" << std::endl;\n\n    return 0;\n}