#include <d3d9.h>\n#pragma comment (lib, "d3d9.lib")\n\nint main() {\n    // Create the Direct3D interface\n    LPDIRECT3D9 d3d = Direct3DCreate9(D3D_SDK_VERSION);\n    if (d3d == NULL) {\n        return 1; // Failed to create Direct3D interface\n    }\n\n    // Set up the structure used to create the device\n    D3DPRESENT_PARAMETERS d3dpp;\n    ZeroMemory(&d3dpp, sizeof(d3dpp));\n    d3dpp.Windowed = TRUE;\n    d3dpp.SwapEffect = D3DSWAPEFFECT_DISCARD;\n    d3dpp.hDeviceWindow = GetConsoleWindow();\n\n    // Create the device\n    LPDIRECT3DDEVICE9 d3ddev;\n    if (d3d->CreateDevice(D3DADAPTER_DEFAULT, D3DDEVTYPE_HAL, GetConsoleWindow(),\n                          D3DCREATE_SOFTWARE_VERTEXPROCESSING, &d3dpp, &d3ddev) != D3D_OK) {\n        d3d->Release();\n        return 1; // Failed to create Direct3D device\n    }\n\n    // Main loop and rendering code would go here\n\n    // Clean up\n    d3ddev->Release();\n    d3d->Release();\n    return 0;\n}