#include <windows.h>\n#include <iostream>\n\nbool IsVCRuntimeInstalled()\n{\n    HKEY hKey;\n    LONG result = RegOpenKeyEx(HKEY_LOCAL_MACHINE, \n        TEXT("SOFTWARE\\Microsoft\\VisualStudio\\8.0\\VC\\VCRedist\\x86"),\n        0, \n        KEY_READ, \n        &hKey);\n\n    if (result == ERROR_SUCCESS)\n    {\n        DWORD dataSize = 0;\n        result = RegQueryValueEx(hKey, TEXT("Installed"), NULL, NULL, NULL, &dataSize);\n        if (result == ERROR_SUCCESS && dataSize > 0)\n        {\n            DWORD installed = 0;\n            result = RegQueryValueEx(hKey, TEXT("Installed"), NULL, NULL, reinterpret_cast<LPBYTE>(&installed), &dataSize);\n            RegCloseKey(hKey);\n            return installed == 1;\n        }\n        RegCloseKey(hKey);\n    }\n\n    return false;\n}\n\nint main()\n{\n    if (IsVCRuntimeInstalled())\n    {\n        std::cout << "Visual C++ Runtime 8.0 SP1 is installed." << std::endl;\n    }\n    else\n    {\n        std::cout << "Visual C++ Runtime 8.0 SP1 is not installed." << std::endl;\n    }\n\n    return 0;\n}