#include <list>\n#include <iostream>\n\nint main() {\n    std::list<int> myList = {1, 2, 3, 4, 5};\n\n    // Store an iterator to the third element\n    auto it = std::next(myList.begin(), 2);\n\n    // Perform some operations, like splicing within the list\n    myList.splice(myList.begin(), myList, it); // Move the third element to the beginning\n\n    // The iterator 'it' should still be valid and point to the element that was moved\n    std::cout << "Element pointed to by iterator: " << *it << std::endl; // Output: 3\n\n    return 0;\n}