#include <rapi.h>\n#include <tchar.h>\n#include <iostream>\n\nint main() {\n    // Initialize RAPI\n    HRESULT hr = CeRapiInit();\n    if (FAILED(hr)) {\n        std::cerr << "Failed to initialize RAPI." << std::endl;\n        return 1;\n    }\n\n    // Path to source file on the desktop\n    LPCWSTR desktopFile = L"C:\\Path\\To\\File.txt";\n    // Path to destination file on the device\n    LPCWSTR deviceFile = L"\\My Documents\\File.txt";\n\n    // Copy file from desktop to device\n    if (!CeCopyFile(desktopFile, deviceFile, FALSE)) {\n        std::cerr << "Failed to copy file." << std::endl;\n        CeRapiUninit();\n        return 1;\n    }\n\n    std::cout << "File copied successfully." << std::endl;\n\n    // Cleanup RAPI\n    CeRapiUninit();\n    return 0;\n}