#include <iostream>\n\nvoid nestedLoops() {\n    const int maxi = 10;\n    const int maxj = 10;\n\n    for (int i = 0; i < maxi; ++i) {\n        for (int j = 0; j < maxj; ++j) {\n            if (i == 4 && j == 3) {\n                return;\n            }\n            // Your code here\n            std::cout << "i: " << i << ", j: " << j << std::endl;\n        }\n    }\n}\n\nint main() {\n    nestedLoops();\n    return 0;\n}