// Example using uWebSockets library\n#include <uWS/uWS.h>\n\nint main() {\n    uWS::Hub hub;\n\n    hub.onMessage([](uWS::WebSocket<uWS::SERVER> *ws, char *message, size_t length, uWS::OpCode opCode) {\n        std::string receivedMessage(message, length);\n        // Process the message\n        std::cout << "Received: " << receivedMessage << std::endl;\n        \n        // Send a response\n        ws->send("Message processed", uWS::OpCode::TEXT);\n    });\n\n    if (hub.listen(3000)) {\n        hub.run();\n    }\n\n    return 0;\n}