#define CATCH_CONFIG_MAIN\n#include <catch2/catch.hpp>\n\nSCENARIO("Addition works correctly", "[calculator]") {\n    GIVEN("A calculator") {\n        Calculator calc;\n\n        WHEN("I add 50 and 70") {\n            calc.enter(50);\n            calc.enter(70);\n            int result = calc.add();\n\n            THEN("The result should be 120") {\n                REQUIRE(result == 120);\n            }\n        }\n    }\n}