#include <iostream>\n#include <fstream>\n#include <cstdlib>\n\nvoid execute(const std::string& code) {\n    std::ofstream file("temp_code.cpp");\n    file << "#include <iostream>\n";\n    file << "int main() {\n";\n    file << code << "\n";\n    file << "return 0;\n";\n    file.close();\n    \n    // Compile the temporary C++ code\n    std::system("g++ temp_code.cpp -o temp_code");\n    \n    // Execute the compiled code\n    std::system("./temp_code");\n    \n    // Clean up\n    std::remove("temp_code.cpp");\n    std::remove("temp_code");\n}\n\nint main() {\n    std::string codeblock1 = "std::cout << \"This is a test\" << std::endl;";\n    std::string codeblock2 = "int array[5] = {0, 6, 6, 3, 5};\nfor(int i = 0; i < 5; ++i) std::cout << array[i] << std::endl;";\n    \n    int i;\n    std::cin >> i;\n    if(i) {\n        execute(codeblock1);\n    } else {\n        execute(codeblock2);\n    }\n    \n    return 0;\n}