#include <windows.h>\n#include <iostream>\n\ntypedef void (__cdecl *TestExceptionHandlingFunc)();\n\nint main() {\n    HMODULE hModule = LoadLibrary(L"MyDll.dll");\n    if (hModule) {\n        TestExceptionHandlingFunc func = (TestExceptionHandlingFunc)GetProcAddress(hModule, "TestExceptionHandling");\n        if (func) {\n            func();\n        } else {\n            std::cerr << "Failed to get function address" << std::endl;\n        }\n        FreeLibrary(hModule);\n    } else {\n        std::cerr << "Failed to load DLL" << std::endl;\n    }\n    return 0;\n}