#include <iostream>\n\ntemplate <class T>\nvoid bar(T* ptr);\n\nclass MyClass  // note that this isn't a template class\n{\nprivate:\n    void foo() {\n        std::cout << "MyClass::foo() called" << std::endl;\n    }\n\n    template <class T>\n    friend void bar(T*);  // Declare the template friend function\n};\n\ntemplate <class T>\nvoid bar(T* ptr)\n{\n    if (ptr)\n    {\n        MyClass obj;\n        obj.foo();\n    }\n}\n\nint main() {\n    int x = 42;\n    bar(&x);\n    return 0;\n}