#include <gtkmm.h>\n\nclass HelloWorld : public Gtk::Application {\nprotected:\n    HelloWorld() : Gtk::Application("com.example.GtkmmApp") {}\n\n    Gtk::Window* create_window() {\n        auto window = new Gtk::Window();\n        window->set_title("Hello, World!");\n        window->set_default_size(200, 200);\n        return window;\n    }\n\n    void on_activate() override {\n        auto window = create_window();\n        add_window(*window);\n        window->show_all();\n    }\n\npublic:\n    static Glib::RefPtr<HelloWorld> create() {\n        return Glib::make_refptr_for_instance<HelloWorld>(new HelloWorld());\n    }\n};\n\nint main(int argc, char *argv[]) {\n    auto app = HelloWorld::create();\n    return app->run(argc, argv);\n}