#include <windows.h>\n#include <iostream>\n\nvoid PrintFileTime(const FILETIME& ft) {\n    // Convert FILETIME to ULARGE_INTEGER to work with raw 64-bit value\n    ULARGE_INTEGER ull;\n    ull.LowPart = ft.dwLowDateTime;\n    ull.HighPart = ft.dwHighDateTime;\n\n    // Print the raw file time value\n    std::cout << "Raw FILETIME: " << ull.QuadPart << std::endl;\n}\n\nint main() {\n    WIN32_FIND_DATA findData;\n    HANDLE hFind = FindFirstFile(L"yourfile.txt", &findData);\n\n    if (hFind != INVALID_HANDLE_VALUE) {\n        // Print raw creation, last access, and last write times\n        std::cout << "Creation Time: ";\n        PrintFileTime(findData.ftCreationTime);\n\n        std::cout << "Last Access Time: ";\n        PrintFileTime(findData.ftLastAccessTime);\n\n        std::cout << "Last Write Time: ";\n        PrintFileTime(findData.ftLastWriteTime);\n\n        FindClose(hFind);\n    } else {\n        std::cerr << "File not found." << std::endl;\n    }\n\n    return 0;\n}