#include <iostream>\nusing namespace std;\n\nclass A {\n    protected:\n    void doSomething(void (A::*someCallback)(int a)) {\n        (this->*someCallback)(1234);\n    }\n};\n\nclass B : public A {\n    public:\n    void runDoIt() { doSomething(static_cast<void (A::*)(int)>(&B::doIt)); }\n    void runDoSomethingElse() { doSomething(static_cast<void (A::*)(int)>(&B::doSomethingElse)); }\n    protected:\n    void doIt(int foo) {\n        cout << "Do It! [" << foo << "]\n";\n    }\n    void doSomethingElse(int foo) {\n        cout << "Do Something Else! [" << foo << "]\n";\n    }\n};\n\nint main(int argc, char *argv[]) {\n    B b;\n    b.runDoIt();\n    b.runDoSomethingElse();\n    return 0;\n}