#include <windows.h>\n#include <lm.h>\n#include <stdio.h>\n\n#pragma comment(lib, "netapi32.lib")\n\nvoid CreateUser() {\n    USER_INFO_1 ui;\n    DWORD level = 1;\n    DWORD err = 0;\n\n    // Set up user information\n    ZeroMemory(&ui, sizeof(ui));\n    ui.usri1_name = L"NewUser";\n    ui.usri1_password = L"Password123";\n    ui.usri1_priv = USER_PRIV_USER;  // Normal user privileges\n    ui.usri1_home_dir = NULL;\n    ui.usri1_comment = NULL;\n    ui.usri1_flags = UF_SCRIPT | UF_DONT_EXPIRE_PASSWD;  // No password change requirement\n    ui.usri1_script_path = NULL;\n\n    // Create the user\n    NET_API_STATUS nStatus = NetUserAdd(NULL, level, (LPBYTE)&ui, &err);\n\n    if (nStatus == NERR_Success) {\n        wprintf(L"User has been successfully created\n");\n    } else {\n        wprintf(L"User creation failed with error: %d\n", nStatus);\n    }\n}\n\nint main() {\n    CreateUser();\n    return 0;\n}