#include <libconfig.h++>\n\nusing namespace libconfig;\n\nint main() {\n    Config cfg;\n\n    try {\n        cfg.readFile("example.cfg");\n    } catch(const FileIOException &fioex) {\n        std::cerr << "I/O error while reading file." << std::endl;\n        return(EXIT_FAILURE);\n    } catch(const ParseException &pex) {\n        std::cerr << "Parse error at " << pex.getFile() << ":" << pex.getLine()\n                  << " - " << pex.getError() << std::endl;\n        return(EXIT_FAILURE);\n    }\n\n    // Example of accessing configuration settings\n    try {\n        int some_setting;\n        cfg.lookupValue("some_setting", some_setting);\n        std::cout << "some_setting: " << some_setting << std::endl;\n    } catch(const SettingNotFoundException &nfex) {\n        std::cerr << "No 'some_setting' setting in configuration file." << std::endl;\n    }\n\n    return(EXIT_SUCCESS);\n}