#include <iostream>\n\nvoid adjustBrightness(unsigned char* image, size_t size, int adjustment) {\n    for (size_t i = 0; i < size; ++i) {\n        // Bitwise AND with 0xFF to ensure no overflow\n        image[i] = static_cast<unsigned char>((image[i] + adjustment) & 0xFF);\n    }\n}\n\nint main() {\n    unsigned char image[5] = {100, 150, 200, 250, 50};\n    size_t size = sizeof(image) / sizeof(image[0]);\n    int adjustment = 10;\n\n    adjustBrightness(image, size, adjustment);\n\n    for (size_t i = 0; i < size; ++i) {\n        std::cout << static_cast<int>(image[i]) << " ";\n    }\n\n    return 0;\n}