#include <iostream>\n\n// Platform-specific includes and definitions\n#ifdef _WIN32\n    #include <windows.h>\n    void clearScreen() {\n        system("cls");\n    }\n#else\n    #include <unistd.h>\n    void clearScreen() {\n        system("clear");\n    }\n#endif\n\nint main() {\n    std::cout << "Hello, cross-platform world!" << std::endl;\n    clearScreen();\n    std::cout << "Screen cleared!" << std::endl;\n    return 0;\n}