#include <windows.h>\n#include <iostream>\n\nint main()\n{\n    TCHAR buffer[512];\n    DWORD ret = GetEnvironmentVariable(_T("PATH"), buffer, 512);\n    if (ret > 0 && ret < 512)\n    {\n        _tprintf(_T("PATH: %s\n"), buffer);\n    }\n    else if (ret >= 512)\n    {\n        _tprintf(_T("Buffer too small; need %d characters\n"), ret);\n    }\n    else\n    {\n        _tprintf(_T("GetEnvironmentVariable failed (%d)\n"), GetLastError());\n    }\n\n    return 0;\n}