#include <iostream>\n#include <vector>\n#include <cstring> // for strcmp\n\nvoid filterFiles(std::vector<const char*>& files, const char* parameter) {\n    auto it = files.begin();\n    while (it != files.end()) {\n        if (std::strcmp(*it, parameter) != 0) {\n            it = files.erase(it);\n        } else {\n            ++it;\n        }\n    }\n}\n\nint main() {\n    std::vector<const char*> files = {"file1.txt", "file2.txt", "file3.txt", "file4.txt"};\n    const char* parameter = "file2.txt";\n\n    filterFiles(files, parameter);\n\n    for (const auto& file : files) {\n        std::cout << file << std::endl;\n    }\n\n    return 0;\n}