#include <iostream>\n#include <fstream>\n#include <string>\n#include <filesystem>\n#include <vector>\n\nusing namespace std;\nnamespace fs = std::filesystem;\n\nint countLOC(ifstream &inFile) {\n    // Placeholder function to count lines of code\n    int lines = 0;\n    string line;\n    while (getline(inFile, line)) {\n        ++lines;\n    }\n    return lines;\n}\n\nint main() {\n    ifstream inFile;\n    int result;\n    string filename;\n\n    cout << "If the executable is not in the same directory as the file, then a directory needs to be provided\n\n";\n    while (true) {\n        cout << "Enter the file name or directory:  ";\n        getline(cin, filename);\n\n        if (fs::is_directory(filename)) {\n            vector<string> textFiles;\n            for (const auto& entry : fs::directory_iterator(filename)) {\n                if (entry.path().extension() == ".txt") {\n                    textFiles.push_back(entry.path().string());\n                }\n            }\n\n            cout << "There are " << textFiles.size() << " text files in the directory \"" << filename << "\"\n";\n            for (const auto& file : textFiles) {\n                cout << file << "\n";\n            }\n            return 0;\n        } else {\n            inFile.open(filename.c_str(), ios::in);\n            if (!inFile) {\n                cout << "\n**File failed to open**\n\n";\n                inFile.clear();\n            } else {\n                break;\n            }\n        }\n    }\n\n    result = countLOC(inFile);\n    cout << "\nThere are " << result << " lines of code in \"" << filename << "\"\n\n";\n\n    inFile.close();\n    return 0;\n}