#include <windows.h>\n#include <iostream>\n\nint main() {\n    // Original UNC path\n    std::wstring uncPath = L"\\\\server\\share\\path\\to\\your\\file.txt";\n    \n    // Add the extended-length path prefix\n    std::wstring extendedPath = L"\\\\?\\UNC" + uncPath.substr(1);\n\n    // Open the file\n    HANDLE hFile = CreateFileW(\n        extendedPath.c_str(),\n        GENERIC_READ,\n        FILE_SHARE_READ,\n        NULL,\n        OPEN_EXISTING,\n        FILE_ATTRIBUTE_NORMAL,\n        NULL\n    );\n\n    if (hFile == INVALID_HANDLE_VALUE) {\n        std::wcerr << L"Failed to open file. Error: " << GetLastError() << std::endl;\n        return 1;\n    }\n\n    std::wcout << L"Successfully opened file: " << extendedPath << std::endl;\n\n    // Do your file operations here\n\n    // Close the file handle\n    CloseHandle(hFile);\n\n    return 0;\n}