#include <iostream>\n\nclass rnObj {\npublic:\n    rnObj() { std::cout << "Constructor\n"; }\n    rnObj(const rnObj&) { std::cout << "Copy Constructor\n"; }\n    rnObj(rnObj&&) noexcept { std::cout << "Move Constructor\n"; }\n    ~rnObj() { std::cout << "Destructor\n"; }\n};\n\nrnObj somefunction() {\n    return rnObj(); // RVO may eliminate the need for a temporary object here\n}\n\nint main() {\n    rnObj obj = somefunction(); // The return value is directly used to initialize 'obj'\n    return 0;\n}