#include <windows.h>\n#include <iostream>\n\nvoid WatchDirectory(const std::wstring& directory) {\n    HANDLE hDir = CreateFileW(\n        directory.c_str(),\n        FILE_LIST_DIRECTORY,\n        FILE_SHARE_READ | FILE_SHARE_WRITE | FILE_SHARE_DELETE,\n        NULL,\n        OPEN_EXISTING,\n        FILE_FLAG_BACKUP_SEMANTICS,\n        NULL\n    );\n\n    if (hDir == INVALID_HANDLE_VALUE) {\n        std::cerr << "CreateFileW failed with error: " << GetLastError() << std::endl;\n        return;\n    }\n\n    char buffer[1024];\n    DWORD bytesReturned;\n\n    while (true) {\n        if (ReadDirectoryChangesW(\n            hDir,\n            buffer,\n            sizeof(buffer),\n            TRUE,\n            FILE_NOTIFY_CHANGE_LAST_WRITE,\n            &bytesReturned,\n            NULL,\n            NULL\n        )) {\n            FILE_NOTIFY_INFORMATION* info = reinterpret_cast<FILE_NOTIFY_INFORMATION*>(buffer);\n            std::wcout << "File modified: " << std::wstring(info->FileName, info->FileNameLength / sizeof(WCHAR)) << std::endl;\n        } else {\n            std::cerr << "ReadDirectoryChangesW failed with error: " << GetLastError() << std::endl;\n            break;\n        }\n    }\n\n    CloseHandle(hDir);\n}\n\nint main() {\n    WatchDirectory(L"C:\\path\\to\\directory");\n    return 0;\n}