#include <windows.h>\n   #include <iostream>\n\n   BOOL CtrlHandler(DWORD fdwCtrlType)\n   {\n       switch (fdwCtrlType)\n       {\n       case CTRL_C_EVENT:\n           std::cout << "Ctrl-C event handled\n";\n           return TRUE; // Return TRUE to indicate the event has been handled\n       default:\n           return FALSE;\n       }\n   }\n\n   int main()\n   {\n       if (SetConsoleCtrlHandler((PHANDLER_ROUTINE)CtrlHandler, TRUE))\n       {\n           std::cout << "The Control Handler is installed.\n";\n       }\n       else\n       {\n           std::cout << "ERROR: Could not set control handler\n";\n           return 1;\n       }\n\n       std::cout << "Press Ctrl-C to trigger the handler...\n";\n       while (true) {} // Keep the program running to test the handler\n       return 0;\n   }