#include <iostream>\n#include <map>\n\nclass Coordinate {\npublic:\n    int x, y;\n\n    Coordinate(int x, int y) : x(x), y(y) {}\n\n    bool operator<(const Coordinate& other) const {\n        // First compare x, if they are equal, compare y\n        return std::tie(x, y) < std::tie(other.x, other.y);\n    }\n};\n\nint main() {\n    // Define the map with Coordinate as the key\n    std::map<Coordinate, int> coordinate_map;\n\n    // Insert some values\n    coordinate_map[Coordinate(12, 45)] = 1;\n    coordinate_map[Coordinate(250000, -250000)] = 2;\n    coordinate_map[Coordinate(-250000, 250000)] = 3;\n\n    // Access the values\n    std::cout << "Value at (12, 45): " << coordinate_map[Coordinate(12, 45)] << std::endl;\n    std::cout << "Value at (250000, -250000): " << coordinate_map[Coordinate(250000, -250000)] << std::endl;\n    std::cout << "Value at (-250000, 250000): " << coordinate_map[Coordinate(-250000, 250000)] << std::endl;\n\n    return 0;\n}