#include <windows.h>\n#include <sddl.h>\n#include <iostream>\n\nvoid GetCurrentUserSID() {\n    HANDLE tokenHandle = NULL;\n    if (OpenProcessToken(GetCurrentProcess(), TOKEN_QUERY, &tokenHandle)) {\n        DWORD tokenInfoLength = 0;\n        GetTokenInformation(tokenHandle, TokenUser, NULL, 0, &tokenInfoLength);\n\n        if (GetLastError() == ERROR_INSUFFICIENT_BUFFER) {\n            PTOKEN_USER tokenUser = (PTOKEN_USER)malloc(tokenInfoLength);\n            if (GetTokenInformation(tokenHandle, TokenUser, tokenUser, tokenInfoLength, &tokenInfoLength)) {\n                LPWSTR sidString = NULL;\n                if (ConvertSidToStringSid(tokenUser->User.Sid, &sidString)) {\n                    wprintf(L"Current user SID: %s\n", sidString);\n                    LocalFree(sidString);\n                }\n            }\n            free(tokenUser);\n        }\n        CloseHandle(tokenHandle);\n    } else {\n        std::cerr << "OpenProcessToken failed. Error: " << GetLastError() << std::endl;\n    }\n}\n\nint main() {\n    GetCurrentUserSID();\n    return 0;\n}