#include <iostream>\n#include <fstream>\n#include <cstdio>  // For std::remove\n\nint main() {\n    // Define the temporary file name\n    const char* tempFileName = "tmp.txt";\n    \n    // Create and write to the temporary file\n    std::ofstream tempFile(tempFileName);\n    if (tempFile.is_open()) {\n        tempFile << "This is a temporary file." << std::endl;\n        tempFile.close();\n        std::cout << "Temporary file created and written to." << std::endl;\n    } else {\n        std::cerr << "Unable to create temporary file." << std::endl;\n        return 1;\n    }\n\n    // Perform other operations as needed\n\n    // Delete the temporary file\n    if (std::remove(tempFileName) == 0) {\n        std::cout << "Temporary file deleted successfully." << std::endl;\n    } else {\n        std::cerr << "Error deleting temporary file." << std::endl;\n        return 1;\n    }\n\n    return 0;\n}