#include <iostream>\n#include <dns_sd.h>\n\nvoid DNSSD_API browseCallback(\n    DNSServiceRef sdRef,\n    DNSServiceFlags flags,\n    uint32_t interfaceIndex,\n    DNSServiceErrorType errorCode,\n    const char *serviceName,\n    const char *regtype,\n    const char *replyDomain,\n    void *context)\n{\n    if (errorCode == kDNSServiceErr_NoError)\n    {\n        std::cout << "Service found: " << serviceName << "." << regtype << replyDomain << std::endl;\n        // Optionally, you can resolve the service to retrieve its IP address and port\n    }\n    else\n    {\n        std::cerr << "Error browsing services: " << errorCode << std::endl;\n    }\n}\n\nint main()\n{\n    DNSServiceRef serviceRef;\n    DNSServiceErrorType error;\n\n    error = DNSServiceBrowse(\n        &serviceRef,\n        0,                  // Flags\n        0,                  // InterfaceIndex\n        "_myservice._tcp",  // Service type\n        NULL,               // Domain\n        browseCallback,     // Callback\n        NULL);              // Context\n\n    if (error != kDNSServiceErr_NoError)\n    {\n        std::cerr << "DNSServiceBrowse error: " << error << std::endl;\n        return 1;\n    }\n\n    DNSServiceProcessResult(serviceRef);\n    DNSServiceRefDeallocate(serviceRef);\n\n    return 0;\n}